function editCategory (state='', action) {
  switch (action.type) {
    case 'EDITCATEGORY':
    console.log(action);
    console.log(action.data);
    // let edit = action.value;
    // let arr = {data: action.data};
    const input = action.data.find(el=> el.id === Number(action.id)).title;
      // const newCategories = {title:action.title, id: uuid()}
      return input.title;

    default:
      return state;
  }
  }

