import uuid from 'uuid/v4';

function historyArr (state=[], action) {
  switch (action.type) {
    case 'saveToHistory':
      const newRecord ={...action.value, id: uuid(), statusMoney: action.statusMoney};

      // localStorage.setItem('contact', JSON.stringify(action.status ? state.map(el=> el.id === action.value.id ? action.value : el) : [...state, newContact]))

