return action.status ? state.map(el=> el.id === action.value.id  ? action.value: el) : [state,newRecord]

      return [...state, newRecord];

    case 'deleteFromHistory':
    // console.log(action.id);
    // console.log(action.data);
    let result = action.data.filter(el=>el.id !== action.id);
    return result;
    default:
      return state;
  }
}

export default historyArr;
