export const saveToHistory = (change, statusMoney, statusButton,) => ({
  type: 'saveToHistory',
  value: change,
  statusMoney: statusMoney,
  status: statusButton,
})

export const deleteFromHistory = (e, historyArr) => ({
type: 'deleteFromHistory',
id: e.target.id,
data: historyArr,
})
