function editCategory (state='', action) {
  switch (action.type) {
    case 'EDITCATEGORY':
    let edit = action.value;
    let input = action.data.find(el=> el.id === edit);
      // const newCategories = {title:action.title, id: uuid()}
      return input.title;

    default:
      return state;
  }
  }

  export default editCategory;
