import uuid from 'uuid/v4';

function categoriesArr (state=[], action) {
  switch (action.type) {
    case 'ADD':
      const newCategories = {title:action.title, id: uuid()}
      return [...state, newCategories];


    default:
      return state;
  }
  }

  export default categoriesArr;
