function statusButton (state=true, action){
  switch (action.type) {
    case 'EDITBUTTON':
      return false;
    // case 'DELETEBUTTON':
    //   return true;
    case 'CANCELBUTTON':
      return false;
    default:
      return state;
  }
}

export default statusButton;
