import React from 'react';
import {connect} from 'react-redux';
import {saveButton} from '../redux/actions/statusButtonAction';
import {cancelButton} from '../redux/actions/statusButtonAction';
import {editButton} from '../redux/actions/statusButtonAction';
import {deleteButton} from '../redux/actions/statusButtonAction';
import './CategoriesListItem.css';

const CategoriesListItem = ({el, status, editButton}) => {
  return (
    <div className='list_item'>
      {/* <p className='list_item_title'>{el.title}</p> */}
{status ? <input type='text' name='title' value={el.title} /> : <p>{el.title}</p>}

      <button onClick={editButton}>{status ? 'save' : 'edit'}</button>
      <button>{status ? 'cancel' : 'delete'}</button>

      {/* <div>
      <li className='ListItem'  >
      {element.edit ? <input type='text' onChange={inputChange} name='input' value={input} className='input-title'/> : <span className={element.status ? 'ListItemCheck' : 'ListItem-text'} id={element.id} onClick={funcCheck}>{element.text} </span>}
       {element.edit ? <Btn text={'Save'} id={element.id} funk={saveFromList} /> : <Btn text={'Edit'} id={element.id} funk={editFromList} />}
       {element.edit ? <Btn text={'Cancel'} id={element.id} funk={cancelFromList} /> : <Btn text={'Delete'} id={element.id} funk={deleteFromList} />} 
      </li> */}

    </div>
  );
};

function mapStateToProps (state) {
  return {
    // flag: state.showModal,
    change: state.change,
    categoriesArr: state.categoriesArr,
    status: state.statusButton,
  }
}


function mapDispatchToProps (dispatch) {
return {


  editButton: function() {
    dispatch(editButton())
  },

  // add: function(e, change) {
  //   e.preventDefault();
  //   dispatch(categoriesAdd(change))
  // },

}
}

export default connect(mapStateToProps, mapDispatchToProps) (CategoriesListItem);
