function editCategory (state='', action) {
  switch (action.type) {
    case 'EDITCATEGORY':
    console.log(action);
  //  let result = action.data.map(el=> el.id === action.id ? null : el )
    const input = action.data.find(el=> el.id === action.id).title;
      return input;
      case 'CHANGECATEGORY':
      return {...state, title: action.value};


      case 'SAVECATEGORY':
      return {state};
    default:
      return state;
  }
  }

  export default editCategory;
