import React from 'react';
import {connect} from 'react-redux';
import {cancel} from '../redux/actions/showModal'
import './ModalWindow.css';

const ModalWindow = ({cansel}) => {
  return (
    <div className='modal'>
    <div className="modal_header">
      <p>ДОХОДИ</p>
      <p>ВИДАТКИ</p>
      </div>
      <form className='form_wrapper'>
      <label >Сума <input type="text"/></label>
      <label >Дата <input type="text"/></label>
      <label >Опис <input type="text"/></label>
      <label >Категорія <input type="text"/></label>
      <div className="button_nav">
<input type="button" value="save"/>
<input type="button" value="cancel" onClick=''/>
</div>
      </form>
    </div>
  );
};

function mapStateToProps (state) {
  return {
    flag: state.showModal,

  }
}


function mapDispatchToProps (dispatch) {
return {
  show: function() {
    dispatch(cancel())
  },
}
}

export default connect(mapStateToProps, mapDispatchToProps) (ModalWindow);
